var myIncident = new GlideRecord('incident');

gs.info("YYY - Workflow for Alarm Update  is firing now.  Going to find existing Alarm and associated Incident.");

// Find a matching Alarm
// It should match on the AlertID

// Alarm has been found, now find the referenced Incident
myIncident.addQuery('sys_id', current.incident);
myIncident.query();

// Find the first Incident returned.  There should only be one...
if(myIncident.next()){
			//Need to extend the conditions around when an incident may be resolved
	   
			gs.info("Found attached Incident: " + myIncident.number + " Found Incident company: " + myIncident.company.name);
			myIncident.state = resolvedState;
			myIncident.close_notes = current.message;
			
			// Closed code: "Closed/Resolved by Caller"
			myIncident.close_code = 2;
			gs.info("Incident has been resolved: " + current.sys_id);
			myIncident.update();
		
		}
		else{
			//What should happen if no incident is associated with the Alarm Update?
			
		}

//Need to extend the conditions around when an incident may be resolved
gs.info("Found attached Incident: " + current.incident.number + " Found Incident company: " + current.incident.company_name);
current.incident.caller_id = current.incident.opened_by;
current.incident.state = resolvedState;
current.incident.close_notes = current.message;
current.incident.short_description = "This ticket will be closed now";

current.incident.close_code = "Closed/Resolved by Caller";
gs.info("Incident has been resolved: " + current.incident.number);
